from types import ClassMethodDescriptorType
from fastapi import FastAPI, HTTPException
from pydantic import BaseModel


class Screen(BaseModel):
    screen: str


class Store:
    __current_screen = ""

    @classmethod
    def set_screen(cls, screentoset: str):
        cls.__current_screen = screentoset

    @classmethod
    def get_screen(cls):
        return cls.__current_screen


app = FastAPI()


@app.post("/screen/")
async def screen_set(item: Screen):
    if len(item.screen) == 8192 and set(item.screen).issubset({"0", "1"}):
        await Store.set_screen(item.screen)
        return {"response": "Success!"}
    else:
        raise HTTPException(
            status_code=400,
            detail="Response Body screen Item lengh is wrong or the string contains something other than 1 or 0",
        )


@app.get("/screen/")
async def screen_give():
    return {"message": Store.get_screen()}
